mapper.select:
select
s.so_id,s.so_theme,s.update_date,s.status,s.con_name,s.con_phone,s.architecture,s.cus_source,s.priority,
s.stage,s.dis_time,s.provider,s.cus_demand,s.signing_time,s.possibility,s.amount,s.star_target,s.del_status,
c.cus_id,c.cus_name,u.u_id,u.u_name
from salesopport s
left join customer c on s.cus_id = c.cus_id
left join user u on s.u_id = u.u_id where 1=1
and s.del_status=1



            and s.stage =#{value}



                and s.status =1


                and s.status =2


                and s.status =3


                and s.status =4


                and s.status =5



            and s.architecture=#{value}


            and s.priority=#{value}


            and s.star_target=#{value}






            and s.so_theme like #{input}


            and c.cus_name like #{input}





order by s.so_id desc

select
s.so_id,s.so_theme,s.update_date,s.status,s.con_name,s.con_phone,s.architecture,s.cus_source,s.priority,
s.stage,s.dis_time,s.provider,s.cus_demand,s.signing_time,s.possibility,s.amount,s.star_target,s.del_status,
c.cus_id,c.cus_name,u.u_id,u.u_name
from salesopport s
left join user u on s.u_id = u.u_id
left join customer c on s.cus_id = c.cus_id

    and s.del_status=1

        and c.cus_id=#{cusId}


        and s.con_name=#{conName}




                and s.so_theme like #{opportunitiesThem}


                and s.so_theme not like #{opportunitiesThem}






        and s.signing_time >=#{signingTimeStart}



        and s.signing_time <=#{signingTimeEnd}





                and s.amount >#{amount}


                and s.amount <#{amount}


                and s.amount >#{amount} and s.amount <#{amount}





        and s.update_date >= #{updateTimeStart}


        and s.update_date <=#{updateTimeEnd}



        and s.cus_source in

                #{customerSource}




        and s.stage in

            #{stage}




        and u.u_id in

            #{user}




        and s.status in

            #{status}




        and s.priority in

            #{priority}



        and s.star_target in

            #{starTarget}




        and s.architecture in

            #{type}




        and s.dis_time >=#{createTimeStart}


        and s.dis_time <=#{createTimeEnd}



order by s.so_id desc

select s.so_id,s.so_theme from salesopport s left join customer c2 on s.cus_id = c2.cus_id

    and s.del_status=1

        and c2.cus_id=#{customer.cusId}

select s.so_id, s.so_theme,s.update_date,s.status,s.con_name, s.con_phone,s.cus_id, s.architecture,s.cus_source,s.priority, s.stage,s.dis_time,s.provider,s.cus_demand,s.signing_time,s.possibility, s.amount,s.star_target,s.del_status, u.u_id,u.u_name from salesopport s left join user u on s.u_id=u.u_id
and s.signing_time>=#{timeStart}
and s.signing_time<=#{timeEnd}
and s.possibility=#{possibolty}
and 1=1
and s.stage!='合同签约'
order by s.so_id desc

select s.so_id, s.so_theme,s.update_date,s.status,s.con_name,
s.con_phone,s.cus_id, s.architecture,s.cus_source,s.priority,
s.stage,s.dis_time,s.provider,s.cus_demand,s.signing_time,s.possibility,
s.amount,s.star_target,s.del_status,
u.u_id,u.u_name
from salesopport s
left join user u on s.u_id=u.u_id


        and s.signing_time>=#{timeStart}


        and s.signing_time<=#{timeEnd}


        and s.possibility=#{possibolty}


        and u.u_id=#{uId}


order by s.so_id desc

select s.stage as stage,count(s.so_id) as counts from salesopport s left join user u on s.u_id=u.u_id
u.u_id=#{uId}
group by s.stage
